import React, { useEffect, useState } from "react";

function App() {
  const [airports, setAirports] = useState([]);
  const [source, setSource] = useState("");
  const [destination, setDestination] = useState("");
  const [itinerary, setItinerary] = useState([]);
  const [loading, setLoading] = useState(false);

  // Fetch airports on mount
  useEffect(() => {
    fetch("http://localhost:8080/api/airports")
      .then((res) => res.json())
      .then((data) => setAirports(data));
  }, []);

  const handleFindPath = () => {
    if (!source || !destination) return;
    setLoading(true);
    fetch(
      `http://localhost:8080/api/shortest-path?source=${source}&destination=${destination}`
    )
      .then((res) => res.json())
      .then((data) => {
        setItinerary(data);
        setLoading(false);
      });
  };

  return (
    <div style={{ maxWidth: 600, margin: "40px auto", fontFamily: "sans-serif" }}>
      <h1>Airport Shortest Path Finder</h1>
      <div style={{ marginBottom: 20 }}>
        <label>
          Source Airport:{" "}
          <select value={source} onChange={(e) => setSource(e.target.value)}>
            <option value="">Select</option>
            {airports.map((a) => (
              <option key={a.code} value={a.code}>
                {a.code} - {a.name}
              </option>
            ))}
          </select>
        </label>
      </div>
      <div style={{ marginBottom: 20 }}>
        <label>
          Destination Airport:{" "}
          <select value={destination} onChange={(e) => setDestination(e.target.value)}>
            <option value="">Select</option>
            {airports.map((a) => (
              <option key={a.code} value={a.code}>
                {a.code} - {a.name}
              </option>
            ))}
          </select>
        </label>
      </div>
      <button onClick={handleFindPath} disabled={loading || !source || !destination}>
        {loading ? "Finding..." : "Find Shortest Path"}
      </button>
      <div style={{ marginTop: 30 }}>
        {itinerary.length > 0 && (
          <div>
            <h2>Itinerary</h2>
            <ul>
              {itinerary.map((step, idx) => (
                <li key={idx}>{step}</li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
